<div class="centered">    <form action="register.php" method="post">        <fieldset>            <div class="form-group">                <input autofocus class="form-control" name="username" placeholder="Username" type="text" required/>            </div>            <div class="form-group">                <input class="form-control" name="password" placeholder="Password" type="password" required/>            </div>            <div class="form-group">                <input class="form-control" name="confirmation" placeholder="Confirm Password" type="password" required/>            </div>            <div class="form-group">                <button type="submit" class="btn btn-default">Register</button>            </div>        </fieldset>    </form>    <div>        or <a href="login.php">Log In</a>    </div></div>